mapper.delete:
delete from db.express

    AND expressNo=#{expressNo}
    AND expressCompany=#{expressCompany}
    AND productLink=#{productlink},
    AND senderTel=#{senderTel}
    AND recipientTel=#{recipientTel}
    AND senderName=#{senderName}
    AND recipientName=#{recipientName}
    AND senderAddress=#{senderAddress}
    AND recipientAddress=#{recipientAddress}
    AND status=#{status}

mapper.update:
update db.express
productLink=#{productLink}, senderTel=#{senderTel}, recipientTel=#{recipientTel}, senderName=#{senderName}, recipientName=#{recipientName}, senderAddress=#{senderAddress}, recipientAddress=#{recipientAddress}, status=#{status},
where expressNo = #{expressNo}

mapper.select:
select *
from db.express e
where e.recipientTel = #{recipientTel}

select *
from db.express e
where e.senderTel = #{senderTel}

select *
from db.express e
where e.recipientTel = #{recipientTel}
  and e.status = #{status}

select *
from db.express e
where e.senderTel = #{senderTel}
  and e.status = #{status}

mapper.update:
update db.express
set status = #{status}
where expressNo = #{expressNo}
